function debounce(timescope) {
  return function (fn) {
    let timeout = null;

    return function () {
      if (timeout) {
        clearTimeout(timeout);
      }

      timeout = setTimeout(() => {
        fn.apply(this, arguments);
      }, timescope);
    };
  };
}

