function throttle(timescope) {
  return function (fn) {
    let cooldown = false;

    return function () {
      if (cooldown) {
        return;
      }

      cooldown = true;
      fn.apply(this.arguments);

      setTimeout(() => {
        cooldown = false;
      }, timescope);
    };
  };
}

