  list: listReducers,
  dict: dictReducers,
  detail: detailReducers,
});

//↓↓↓等价于↓↓↓

export default function (state, action) {
  return {
    list: listReducers(state.list, action),
    dict: dictReducers(state.dict, action),
    detail: detailReducers(state.detail, action),
  };
}

