class A {}
class B extends A {}

var A = function A() {};

var B = (function B(_A) {
  //super相当于new关键字
  var super = function () {
    var Super = _B.__proto__;
    var result = Super.apply(this, arguments);

    if (
      result &&
      (_typeof(result) === "object" || typeof result === "function")
    ) {
      return result;
    }
    return this;
  };

  function _B() {
    //相当于new B(...arguments)
    return super.apply(this, arguments);
  }

  //继承_A的prototype，并将原型指向_A
  _B.prototype = {
    ..._A.prototype,
    constructor: _B,
  };
  _B.__proto__ = _A;

  return _B;
})(A);
